import React from 'react';

const WeatherCard = ({ data }) => {
  let backgroundImage;

  switch (data.description.toLowerCase()) {
    case 'sunny':
      backgroundImage = 'url(/path/to/sunny.jpg)';
      break;
    case 'rain':
      backgroundImage = 'url(/path/to/rain.jpg)';
      break;
    case 'cloudy':
      backgroundImage = 'url(/path/to/cloudy.jpg)';
      break;
    case 'snow':
      backgroundImage = 'url(/path/to/snow.jpg)';
      break;
    case 'storm':
      backgroundImage = 'url(/path/to/storm.jpg)';
      break;
    case 'fog':
      backgroundImage = 'url(/path/to/fog.jpg)';
      break;
    case 'clear':
      backgroundImage = 'url(/path/to/clear.jpg)';
      break;
    default:
      backgroundImage = 'url(/path/to/default.jpg)';
      break;
  }

  return (
    <div
      className="bg-white rounded-lg shadow-lg p-6 mt-4"
      style={{ backgroundImage, backgroundSize: 'cover', backgroundPosition: 'center' }}
    >
      <h2 className="text-2xl font-bold mb-2">{data.city}</h2>
      <div className="grid grid-cols-2 gap-4">
        <div>
          <p className="text-gray-600">Temperature</p>
          <p className="text-xl">{data.temperature}Â°C</p>
        </div>
        <div>
          <p className="text-gray-600">Humidity</p>
          <p className="text-xl">{data.humidity}%</p>
        </div>
        <div>
          <p className="text-gray-600">Wind Speed</p>
          <p className="text-xl">{data.wind_speed} m/s</p>
        </div>
        <div>
          <p className="text-gray-600">Conditions</p>
          <p className="text-xl capitalize">{data.description}</p>
        </div>
      </div>
      {data.cached && (
        <p className="mt-4 text-sm text-blue-500">Served from cache</p>
      )}
    </div>
  );
};

export default WeatherCard;
