import React, { useState } from 'react';
import SearchBar from './components/SearchBar';
import WeatherCard from './components/WeatherCard';
import WeatherHistory from './components/WeatherHistory';
import WeatherTrends from './components/WeatherTrends';
import { getWeather, getWeatherHistory, getWeatherTrends } from './api';
import './styles.css';

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [history, setHistory] = useState(null);
  const [trends, setTrends] = useState(null);
  const [error, setError] = useState('');
  const [days, setDays] = useState(7); // Default to past 7 days

  const fetchWeather = async () => {
    try {
      const data = await getWeather(city);
      setWeather(data);
      setHistory(null);
      setTrends(null);
      setError('');
    } catch (err) {
      setError(err.message);
    }
  };

  const fetchHistory = async () => {
    try {
      const data = await getWeatherHistory(city, days);
      setHistory(data);
      setWeather(null);
      setTrends(null);
      setError('');
    } catch (err) {
      setError(err.message);
    }
  };

  const fetchTrends = async () => {
    try {
      const data = await getWeatherTrends(city, days);
      setTrends(data);
      setWeather(null);
      setHistory(null);
      setError('');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-md mx-auto">
        <h1 className="text-3xl font-bold mb-4 text-center">Weather Monitor</h1>
        <SearchBar city={city} onCityChange={(e) => setCity(e.target.value)} onSearch={fetchWeather} />

        {/* Filter Dropdown for Days */}
        <div className="mb-4 text-center">
          <label className="mr-2">Select Days:</label>
          <select 
            value={days} 
            onChange={(e) => setDays(e.target.value)} 
            className="border p-2 rounded"
          >
            <option value="7">Last 7 days</option>
            <option value="14">Last 14 days</option>
          </select>
        </div>

        <div className="flex gap-2 mb-4">
          <button onClick={fetchHistory} className="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600">History</button>
          <button onClick={fetchTrends} className="bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600">Trends</button>
        </div>

        {error && <p className="text-red-500 text-center">{error}</p>}
        {weather && <WeatherCard data={weather} />}
        {history && <WeatherHistory history={history} />}
        {trends && <WeatherTrends trends={trends} />}
      </div>
    </div>
  );
}

export default App;
